void app_main(void)
{
    //zero-initialize the config structure.
    gpio_config_t io_conf = {};
    //disable interrupt
    io_conf.intr_type = GPIO_INTR_DISABLE;
    //set as output mode
    io_conf.mode = GPIO_MODE_OUTPUT;
    //bit mask of the pins that you want to set,e.g.GPIO18/19
    io_conf.pin_bit_mask = GPIO_OUTPUT_PIN_SEL;
    //disable pull-down mode
    io_conf.pull_down_en = 0;
    //disable pull-up mode
    io_conf.pull_up_en = 0;
    //configure GPIO with the given settings
    gpio_config(&io_conf);

    Tm_Initialize_Timer_0();

    Tm_Periodo periodos[3];
    Tm_Timeout timeouts[3];

    Bf_data data[64] = {};

    Bf_data temp = 20;

    Bf_Inicie(&c_buff,data,SIZE_BUFFER);
    Tm_Inicie(&c_tiempo,periodos,3,timeouts,3,Atender_timer);

    Dp_Inicie (&c_display,0,0,1,2);

    Tm_Inicie_periodo (&c_tiempo,0,1);

    uart0_config(2400,17,16); // Configura UART0 a 2400 baudios tx 17 y rx 16

    char xon = 0; // 0 cuando se enva xon y 1 cuando se enva xoff
    char xoff = 0;

    esp_task_wdt_deinit();
    
    while (1) {
        
        if ( Atender_timer(NO) )
            Tm_Procese(&c_tiempo);

        if (Tm_Hubo_periodo(&c_tiempo,0) )
        {
            Dp_Procese (&c_display);
            Tm_Baje_periodo (&c_tiempo,0);
        }

        if(!Bf_Lleno(&c_buff))
        {
                if(UART0.status.rxfifo_cnt > 0)
                {
                    Bf_Subir_Dato(&c_buff,uart0_receive_byte());
                }
        }
        

        if(Bf_75_Lleno(&c_buff) && !xoff)
        {
            uart0_send_byte(XOFF);
            xoff = 1;
            xon = 0;
        }

        else if(Bf_75_Vacio(&c_buff) && !xon)
        {
            uart0_send_byte(XON);
            xon = 1;
            xoff = 0;
        }
    }
}
